import React, { useContext, useReducer, useState } from 'react';
import '../styles/SigninScreen.css';
import Axios from 'axios';
import { getError } from '../utils.js';
import { Store } from '../Store.js';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import ReactLoading from 'react-loading';
import Video from '../assets/login_back.mp4';



const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return { ...state, loading: false };
    case 'FETCH_FAILED':
      return { ...state, loading: false };
  }
};

export default function SigninScreen() {
  const [rollno, setRollno] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [department, setDepartment] = useState('Civil Engineering');
  const [year, setYear] = useState('2');
  const [section, setSection] = useState('A');

  const { state, dispatch: ctxDispatch } = useContext(Store);

  const [{ loading }, dispatch] = useReducer(reducer, { loading: false });

  const navigate = useNavigate();

  const registerHandler = async (e) => {
    e.preventDefault();
    try {
      dispatch({ type: 'FETCH_REQUEST' });
      const { data } = await Axios.put('/users/signin', {
        rollno,
        name,
        email,
        department,
        year,
        section,
      });
      ctxDispatch({ type: 'USER_SIGNIN', payload: data });
      localStorage.setItem('userInfo', JSON.stringify(data));
      dispatch({ type: 'FETCH_SUCCESS' });
      toast.success(data.name + ' loggedin successfully!');
      navigate('/guidelines');
    } catch (err) {
      dispatch({ type: 'FETCH_FAILED' });
      toast.error(getError(err));
    }
  };

  return (
    <div className="sign-in-page">


<video className='w-100' src={Video} autoPlay loop muted />


      {loading && (
        <div className="overlay1">
