  //   </div>
  // );




  const containerStyle = {
    perspective: '23vmin',
    background: `radial-gradient(
        circle,
        #00b8a9,
        #f8f3d4,
        #f6416c,
        #ffde7d
      )`,
    backgroundSize: '100% 100%',
    backgroundRepeat: 'no-repeat',
    borderRadius: '50%',
    transformStyle: 'preserve-3d',
    animation: 'scale-up 20s linear infinite',
  };

  const keyframes = {
    '0%': {
      opacity: 0,
      transform: 'translate3d(0, 0, 0) rotate(0)',
    },
    '10%': {
      opacity: 1,
    },
    '95%': {
      transform: `translate3d(0, 0, ${getRandomArbitrary(50, 55)}vmin) rotate(${getRandomArbitrary(
        -360,
        360
      )}deg)`,
    },
    '100%': {
      opacity: 0,
      transform: 'translate3d(0, 0, 1vmin)',
    },
  };

  const getRandomArbitrary = (min, max) => {
    return Math.random() * (max - min) + min;
  };

  return (
    <div style={{ ...containerStyle, width: '80%', height: '80%', margin: 'auto' }}>
      {[...Array(50)].map((_, index) => (
        <div
          key={index}
          style={{
            width: '50vmin',
            height: '1vmin',
            margin: 'auto',
            animation: 'scale-up 20s linear infinite',
            animationDelay: `calc(${index} * -.4s)`,
            backgroundColor: `rgb(${getRandomArbitrary(200, 240)}, ${getRandomArbitrary(
              200,
              240
            )}, ${getRandomArbitrary(200, 240)})`,
          }}
        />
      ))}
      <style>{`
        @keyframes scale-up {
          ${Object.entries(keyframes)
            .map(([key, value]) => `${key} { ${Object.entries(value).map(([k, v]) => `${k}: ${v};`).join('\n')} }`)
            .join('\n')}
        }
      `}</style>
    </div>
  );
}
